create policy "Can only view own subs data."
on "public"."subscriptions"
as permissive
for select
to public
using ((( SELECT auth.uid() AS uid) = user_id));


create policy "Can update own user data."
on "public"."users"
as permissive
for update
to public
using ((( SELECT auth.uid() AS uid) = id));


